//==============================================================================
void EmbeddedImage::paint (Graphics& g)
{
    //[UserPrePaint] Add your own custom painting code here..
    //[/UserPrePaint]

    g.fillAll (Colours::white);

    //[UserPaint] Add your own custom painting code here..
    Path wavePath;
    
    const float binStep = 18.0f;
    const float amplitudeStep = 4.0f;
    int xOffset = 12;
    int yOffset = 190;
    
    // Iterate over frequency bins.
    for (float x = xOffset + binStep * 0.5f, i = 0; x < xOffset + 8 * binStep; x += binStep, i++)
    {
        
        // Get level from bin
//        int barCount = int(fftBins[int(i)] * 10);
        int barCount = fftBins[int(i)] < 40 ? fftBins[int(i)] : 40;
        
        // Iterate over level for each bin.
        for (int y = yOffset; y > yOffset - barCount * amplitudeStep; y -= amplitudeStep) {
            
            wavePath.addLineSegment (Line<float> (x, y, x + 16.f, y), 2.0f);
            
        }

    }
    
    g.setColour (Colours::orange);
    g.fillPath (wavePath);
    
    AffineTransform translate = AffineTransform::translation(2, 7);
    g.setColour (Colours::white);
    g.fillPath (maskPath, translate);
    
    g.setColour (Colours::orange);
    g.fillPath (logoPath, RectanglePlacement (RectanglePlacement::stretchToFit)
                .getTransformToFit (logoPath.getBounds(),
                                    getLocalBounds().toFloat()));
    

    
    //[/UserPaint]
}

